CREATE TABLE IF NOT EXISTS  `{$prefix}file_attr{$suffix}` (`fla_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主建ID',`at_id` int(10) NOT NULL COMMENT '文件名称',`fla_type` tinyint(2) NOT NULL COMMENT '文件类别 1为文件夹，2为文件',`fla_icon` tinyint(3) NOT NULL COMMENT '文件类型 1.为word ,2.为excel,3.为ppt',`is_parent` tinyint(2) NOT NULL COMMENT '是否为父类 0为是，1为否',`parent_id` mediumint(10) NOT NULL COMMENT '父类ID',`fla_level` mediumint(10) NOT NULL COMMENT '文件目录级别',`m_uid` int(10) NOT NULL COMMENT '用户ID',`fla_status` tinyint(3) NOT NULL DEFAULT '0' COMMENT '记录状态, 1=正常; 2=预删除; 3=已删除',`fla_created` int(10) NOT NULL DEFAULT '0' COMMENT '创建时间',`fla_updated` int(10) NOT NULL DEFAULT '0' COMMENT '更新时间',`fla_deleted` int(10) NOT NULL DEFAULT '0' COMMENT '删除时间',PRIMARY KEY (`fla_id`))ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='文件表';CREATE TABLE IF NOT EXISTS  `{$prefix}file_setting{$suffix}` (`fls_key` varchar(50) NOT NULL COMMENT '变量名',`fls_value` text NOT NULL COMMENT '值',`fls_type` tinyint(3) NOT NULL COMMENT '缓存类型, 0:非数组, 1:数组',`fls_comment` text NOT NULL COMMENT '说明',`fls_status` tinyint(3) NOT NULL COMMENT '记录状态, 1初始化，2=已更新, 3=已删除',`fls_created` int(10) unsigned NOT NULL COMMENT '创建时间',`fls_updated` int(10) unsigned NOT NULL COMMENT '更新时间',`fls_deleted` int(10) unsigned NOT NULL COMMENT '删除时间',PRIMARY KEY (`fls_key`))ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='设置表';CREATE TABLE IF NOT EXISTS  `{$prefix}file_record{$suffix}` (`flr_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '主键ID',`fla_id` int(10) NOT NULL COMMENT '文件ID',`flr_down` tinyint(2) NOT NULL COMMENT '是否下载 文件 0为否 ，1为是',`flr_status` tinyint(2) NOT NULL COMMENT '记录状态, 1=正常，2=已更新, 3=已删除',`flr_created` int(10) NOT NULL COMMENT '生成记录时间',`flr_updated` int(10) NOT NULL COMMENT '更新记录时间',`frl_deleted` int(10) NOT NULL COMMENT '删除时间',PRIMARY KEY (`flr_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户操作文件表';CREATE TABLE IF NOT EXISTS  `{$prefix}file_share{$suffix}` (`fls_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增ID',`fla_id` int(11) NOT NULL COMMENT '文件ID',`m_uid` int(11) NOT NULL COMMENT '用户UID, 为 0 时, 全体成员可看',`m_username` varchar(54) NOT NULL COMMENT '用户名',`fls_status` tinyint(3) unsigned NOT NULL COMMENT '记录状态, 1=初始化，2=已更新, 3=抄送人，4=已删除',`fls_created` int(10) unsigned NOT NULL,`fls_updated` int(10) unsigned NOT NULL,`fls_deleted` int(10) unsigned NOT NULL,PRIMARY KEY (`fls_id`))ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='用户分享文件';